import React, { Component } from 'react' 

export default class Form extends Component {

renderSelect = () => {
    let filtered = this.props.categories.filter(c => c !== "All")

    return ( 
        <select name="category">
            {filtered.map(c => {
                return <option>{c}</option>
            })}
        </select>
    )
    }

    handleSubmit = (e) => {
        e.preventDefault()
        this.props.addTask(e.target.text.value, e.target.category.value)
    }

    render(){
        return(
        <form className="new-task-form" onSubmit={this.handleSubmit}>
                <input type="text" name="text" placeholder="New task details"></input>
                {this.renderSelect()}
                <input type="submit" value="Add task"></input>
        </form>

        )
    }



}
