import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
// import Category from './Category.js'

class App extends React.Component {

  state = {
    tasks: [
      {text: 'Buy rice',
        category: 'Food'},
      { text: 'Save a tenner',
        category: 'Money'},
      { text: 'Build a todo app',
        category: 'Code'},
      {text: 'Build todo API',
        category: 'Code'},
      {text: 'Get an ISA',
        category: 'Money'},
      { text: 'Cook rice', category: 'Food'
      },
      { text: 'Tidy house',
        category: 'Misc'}
      ],
      selectedCategory: "All"
  }

  displayFilterButtons = () => {
   return CATEGORIES.map(c => {
      return <button onclick={this.handleClick}>{c}</button>
    })
  }

  handleClick = (e) => {
    debugger
  }



  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
          <div className="categories">
            <h5>Category filters</h5>
            {this.displayFilterButtons()}
          </div>
          <div className="tasks">
            <h5>Tasks</h5>
          </div>
      </div>
    );
  }
}


export default App;
