import React from 'react';
import './App.css';
import { CATEGORIES } from './data'

class App extends React.Component {

  state = {
    tasks: [
      {text: 'Buy rice',
        category: 'Food'},
      { text: 'Save a tenner',
        category: 'Money'},
      { text: 'Build a todo app',
        category: 'Code'},
      {text: 'Build todo API',
        category: 'Code'},
      {text: 'Get an ISA',
        category: 'Money'},
      { text: 'Cook rice', category: 'Food'
      },
      { text: 'Tidy house',
        category: 'Misc'}
      ],
      selectedCategory: "All"
  }

  displayFilterButtons(){
    CATEGORIES.map(c => {
      return <button className=".categories">{c}</button> 
    })
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        {this.displayFilterButtons()}
      </div>
    );
  }
}


export default App;
