import React, { Component } from 'react' 


export default class Category extends Component {

    constructor(){
        super()
        this.state = ({
            className: ""
        })
    }

    handleClick = (e) => {
        this.setState({
            className: "selected"
        })
        this.props.click(e)
    }

    render(){
        return(<button classonClick={this.handleClick}>{this.props.text}</button>)

    }



}
