import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
// import Category from './Category.js'

class App extends React.Component {

  state = {
    tasks: [
      {text: 'Buy rice',
        category: 'Food'},
      { text: 'Save a tenner',
        category: 'Money'},
      { text: 'Build a todo app',
        category: 'Code'},
      {text: 'Build todo API',
        category: 'Code'},
      {text: 'Get an ISA',
        category: 'Money'},
      { text: 'Cook rice', category: 'Food'
      },
      { text: 'Tidy house',
        category: 'Misc'}
      ],
      selectedCategory: "All"
  }

  displayFilterButtons = () => {
   return CATEGORIES.map(c => {
      return <button onClick={this.handleClick}>{c}</button>
    })
  }

  handleClick = (e) => {
    this.setState({selectedCategory: e.target.innerText})
  }

  renderTasks = () => {
    let filtered
    if(this.state.selectedCategory === "All"){
      filtered = this.state.tasks
    }

   filtered =  this.state.tasks.filter(task => {
     return task.category === this.state.selectedCategory
    })

    return filtered.map(task => {
     return <div className="task">
        <div className="label">{task.category}</div>
        <div className="text">{task.text}</div>
      </div>
    })

  }


  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
          <div className="categories">
            <h5>Category filters</h5>
            {this.displayFilterButtons()}
          </div>
          <div className="tasks">
            <h5>Tasks</h5>
              <div>
                {this.renderTasks()}
              </div>
          </div>
      </div>
    );
  }
}


export default App;
