import React, { Component } from 'react' 


export default class Category extends Component {

    constructor(){
        super()
        this.state = {
            selected: "All"
        }
    }

    handleClick = (e) => {
        this.setState({
            selected: e.target.innerText
        })
        this.props.click(e)
    }



    render(){
        return(<button onClick={ this.props.click} className={this.state.selected === this.props.text ? "selected" : null}>{this.props.text}</button>)

    }



}
